INSERT INTO `wp_wc_admin_notes` VALUES("1", "wc-admin-welcome-note", "info", "en_US", "Nouvelle(s) fonctionnalité(s)", "Bienvenue dans la nouvelle expérience WooCommerce ! Cette nouvelle version vous offrira un aperçu de votre boutique dans le tableau de bord. Elle vous permettra également de gérer des aspects importants de votre activité (commandes, stock, avis, etc.) depuis n\'importe quelle partie de l\'interface et d\'explorer les données de votre boutique au moyen d\'une nouvelle section de statistiques et plus encore !", "info", "{}", "unactioned", "woocommerce-admin", "2020-05-27 14:31:41", NULL, "0");
INSERT INTO `wp_wc_admin_notes` VALUES("2", "wc-admin-store-notice-setting-moved", "info", "en_US", "Vous recherchez le paramètre Annonce de la boutique ?", "Vous le trouverez désormais dans l\'outil de personnalisation.", "info", "{}", "unactioned", "woocommerce-admin", "2020-05-27 14:31:41", NULL, "0");
INSERT INTO `wp_wc_admin_notes` VALUES("3", "wc-admin-wc-helper-connection", "info", "en_US", "Se connecter à WooCommerce.com", "Connectez-vous pour obtenir des notifications et des mises à jour importantes sur les produits.", "info", "{}", "unactioned", "woocommerce-admin", "2020-05-27 14:31:42", NULL, "0");
INSERT INTO `wp_wc_admin_notes` VALUES("4", "wc-admin-onboarding-profiler-reminder", "update", "en_US", "Bienvenue dans WooCommerce ! Configurer votre boutique et commencer à vendre", "Notre objectif est de vous aider tout au long des étapes les plus importantes du lancement de votre boutique.", "info", "{}", "actioned", "woocommerce-admin", "2020-05-29 06:23:58", NULL, "0");
INSERT INTO `wp_wc_admin_notes` VALUES("5", "wc-admin-mobile-app", "info", "en_US", "Installer l\'application mobile Woo", "Installez l\'application mobile WooCommerce pour gérer les commandes, recevoir des notifications de vente et afficher les statistiques principales où que vous soyez.", "phone", "{}", "unactioned", "woocommerce-admin", "2020-05-29 14:33:16", NULL, "0");
INSERT INTO `wp_wc_admin_notes` VALUES("6", "wc-admin-onboarding-email-marketing", "info", "en_US", "Astuces, mises à jour des produits et inspiration", "Nous sommes à votre disposition. Profitez de conseils, de mises à jour des produits et de sources d\'inspiration directement dans votre boîte de messagerie.", "mail", "{}", "unactioned", "woocommerce-admin", "2020-05-29 14:33:16", NULL, "0");
INSERT INTO `wp_wc_admin_notes` VALUES("7", "wc-admin-store-notice-giving-feedback", "info", "en_US", "Donner votre avis sur votre expérience", "Si vous aimez WooCommerce Admin, n\'hésitez pas à nous laisser une note de 5 étoiles. Un grand merci d\'avance !", "info", "{}", "unactioned", "woocommerce-admin", "2020-06-02 07:06:05", NULL, "0");
INSERT INTO `wp_wc_admin_notes` VALUES("8", "wc-admin-facebook-extension", "info", "en_US", "Market on Facebook", "Développez votre activité en ciblant la clientèle et en augmentant les ventes avec Facebook. Vous pouvez désormais installer cette extension gratuite.", "thumbs-up", "{}", "unactioned", "woocommerce-admin", "2020-06-02 07:06:05", NULL, "0");
INSERT INTO `wp_wc_admin_notes` VALUES("9", "wc-admin-personalize-store", "info", "en_US", "Personnalisez la page d’accueil de votre boutique", "La page d’accueil est l’un des points d’accès à votre boutique les plus importants. Si elle est bien faite, elle peut vous permettre d’obtenir plus de conversions et d’engagement. N’oubliez pas de personnaliser la page d’accueil que nous avons créée pour votre boutique pendant l’intégration.", "notice", "{}", "unactioned", "woocommerce-admin", "2020-06-02 07:06:05", NULL, "0");
